package Lec1;

public class Arithmetic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 8;
		int b = 3;
		int c = a + b;
		System.out.println(c);
		c = a - b;
		System.out.println(c);
		int d = a % b;
		System.out.println(d);
		// Assignment Operators
		int x = 5;
		x += 5;// x = x+ 5;
		x = +5;// x= 5

	}

}
