package Lec2;

public class Maximum_3_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 7;
		int c = 6;
		if (a >= b && a >= c) {
			System.out.println(a);
		} 
		else if (b >= a && b >= c) {
			System.out.println(b);
		}
		else {
			System.out.println(c);
		}

	}

}
